  changedDate:any = '';
  pipe = new DatePipe('en-US');
  changeFormat(today:any){
    let ChangedFormat = this.pipe.transform(this.today, 'YYYY/MM/dd');
    this.changedDate = ChangedFormat;
    console.log(this.changedDate);
  }

}
